import { Page } from '@playwright/test';

/**
 * Helper to upload a test file
 */
export async function uploadTestFile(
  page: Page,
  fileName: string,
  mimeType: string,
  content: Buffer
) {
  const fileInput = page.locator('input[type="file"]');
  await fileInput.setInputFiles({
    name: fileName,
    mimeType,
    buffer: content,
  });
}

