/**
 * Helper to create a minimal PDF buffer
 */
export function createTestPdf(pageCount: number = 1): Buffer {
  // Create a minimal PDF with specified page count
  let pages = '';
  for (let i = 0; i < pageCount; i++) {
    pages += `/Type /Page\n`;
  }

  const pdfContent = `%PDF-1.4
1 0 obj
<< /Type /Catalog /Pages 2 0 R >>
endobj
2 0 obj
<< /Type /Pages /Kids [3 0 R] /Count ${pageCount} >>
endobj
3 0 obj
<< /Type /Page /Parent 2 0 R /MediaBox [0 0 612 792] >>
endobj
xref
0 4
trailer
<< /Size 4 /Root 1 0 R >>
startxref
%%EOF`;

  return Buffer.from(pdfContent);
}

/**
 * Helper to wait for order creation and get code
 */
export async function waitForOrderCode(page: Page): Promise<string | null> {
  try {
    const codeElement = await page.waitForSelector(
      'text=/[A-Z0-9]{3}\\s*[A-Z0-9]{3}/',
      { timeout: 10000 }
    );
    const text = await codeElement?.textContent();
    return text?.replace(/\s/g, '') || null;
  } catch {
    return null;
  }
}

/**
 * Helper to navigate through photo flow
 */
export async function navigatePhotoFlow(page: Page) {
  // 1. Go to fotos
  await page.goto('/fotos');

  // 2. Upload test image
  await uploadTestFile(page, 'test.jpg', 'image/jpeg', createTestJpeg());
  await page.waitForTimeout(1000);

  // 3. Continue to layout
  const continueBtn = page.getByRole('button', { name: /continuar/i });
  if (await continueBtn.isEnabled()) {
    await continueBtn.click();
  }
}

/**
 * Helper to clear session storage
 */
export async function clearSession(page: Page) {
  await page.evaluate(() => {
    sessionStorage.clear();
    localStorage.clear();
  });
}
